# This is for part 2 only

from random import randint

inputs = [line.strip().split(" => ")[::-1] for line in open("day19.txt").readlines()]
inputs = [(value[0], value[1]) for value in inputs]

result = ""
replacements = 0

while result != "e":
    input, replace = inputs[randint(0, len(inputs) - 1)]

    new_result = result.replace(input, replace, 1)

    if result == new_result:
        continue

    result = new_result
    replacements += 1

print(replacements)

